sqlMap.select:
SELECT * FROM ( SELECT ROW_NUMBER() OVER(TB.NTT_ID) RN, TB.* FROM ( SELECT a.NTT_ID, a.NTT_SJ, a.NTT_CN, a.FRST_REGISTER_ID, ISNULL('admin', a.NTCR_NM) as FRST_REGISTER_NM, CONVERT(CHAR(10), a.FRST_REGISTER_PNTTM, 120) as FRST_REGISTER_PNTTM, a.INQIRE_CO, a.PARNTS, a.REPLY_AT, a.REPLY_LC, a.USE_AT, a.ATCH_FILE_ID, a.BBS_ID, a.NTCE_BGNDE, a.NTCE_ENDDE, a.SECRET, a.OPTION1, a.OPTION2, a.OPTION3, a.OPTION4, a.OPTION5, a.OPTION6, a.OPTION7, a.OPTION8, a.OPTION9, a.OPTION10, a.OPTION11, a.OPTION12, a.OPTION13, a.OPTION14, a.OPTION15, a.OPTION16, a.OPTION17, a.OPTION18, a.OPTION19, a.OPTION20, (SELECT top 1 D.STRE_FILE_NM FROM COMTNFILE c, COMTNFILEDETAIL d WHERE c.ATCH_FILE_ID = a.ATCH_FILE_ID AND c.ATCH_FILE_ID = d.ATCH_FILE_ID AND c.USE_AT = 'Y') as FILENM, ISNULL((SELECT top 1 D.FILE_SN FROM COMTNFILE c, COMTNFILEDETAIL d WHERE c.ATCH_FILE_ID = a.ATCH_FILE_ID AND c.ATCH_FILE_ID = d.ATCH_FILE_ID AND c.USE_AT = 'Y'),0) as FILESEQ, a.DEPT, a.DONG, 0 AS COMMENT_CNT FROM COMTNBBS a WHERE a.DELCODE = '0' AND a.FRST_REGISTER_ID = #frstRegisterId#
$searchCnd$ LIKE '%' + #searchWrd# + '%'
) TB ) WHERE rn BETWEEN #firstIndex# + 1 AND #firstIndex# + #recordCountPerPage#
