sqlMap.select:
/* FreecheckAdminDAO.selectIndustryListTotCnt */
SELECT ISNULL(COUNT(*),0) AS CNT FROM HETB_CK_INDUSTRY WHERE STATUS = 'N'
AND DIVIDE_CD LIKE '%$searchKeyword$%'

/* FreecheckAdminDAO.selectIndustryListTotCnt2 */
SELECT ISNULL(SUM(INDUSTRY_CNT),0) AS CNT FROM HETB_CK_INDUSTRY A RIGHT OUTER JOIN ( SELECT INDUSTRY_CD, COUNT(*) INDUSTRY_CNT FROM HETB_CK_COMPANY WHERE STATUS = 'N' GROUP BY INDUSTRY_CD ) B ON A.INDUSTRY_CD = B.INDUSTRY_CD WHERE A.STATUS = 'N'
AND A.DIVIDE_CD LIKE '%$searchKeyword$%'

/* FreecheckAdminDAO.selectIndustryList */
SELECT A.* FROM ( SELECT ROW_NUMBER() OVER(ORDER BY ALL_LIST.DIVIDE_CD ASC, INDUSTRY_CD ASC) RNUM , ALL_LIST.* FROM ( SELECT A.INDUSTRY_CD , A.DIVIDE_CD , A.NAME , A.USE_YN , CASE A.USE_YN WHEN 'Y' THEN '정상' WHEN 'N' THEN '폐업' END USE_YN_NAME , INSERT_DT , ISNULL(B.INDUSTRY_CNT, 0) INDUSTRY_CNT , A.YEAR_CD FROM HETB_CK_INDUSTRY A RIGHT OUTER JOIN ( SELECT INDUSTRY_CD, COUNT(*) INDUSTRY_CNT FROM HETB_CK_COMPANY WHERE STATUS = 'N' GROUP BY INDUSTRY_CD ) B ON A.INDUSTRY_CD = B.INDUSTRY_CD WHERE A.STATUS = 'N'
AND A.DIVIDE_CD LIKE '%$searchKeyword$%'
) ALL_LIST ) A WHERE A.RNUM > 0 AND A.RNUM <= 10000

/* FreecheckAdminDAO.checkCompanyId */
SELECT COUNT(*) FROM HETB_CK_COMPANY WHERE ID LIKE '$id$' AND STATUS = 'N'
AND COMPANY_CD <> $company_cd$

/* FreecheckAdminDAO.selectCompanyNextKey */
	
		SELECT		dbo.UFN_LPAD(MAX(COMPANY_CD) +1, 4, '0') as key3
		FROM 		HETB_CK_COMPANY

sqlMap.delete:
/* FreecheckAdminDAO.deleteAnswerForCompanyId */
DELETE FROM HETB_CK_ANSWER WHERE COMPANY_ID = '$id$'
AND MASTER_CD = $master_cd$

/* FreecheckAdminDAO.deleteAnswer2ForCompanyId */
DELETE FROM HETB_CK_ANSWER2 WHERE COMPANY_ID = '$id$'
AND MASTER_CD = $master_cd$

sqlMap.select:
/* FreecheckAdminDAO.selectCheckedCompanyList */
SELECT A.* FROM ( SELECT ROW_NUMBER() OVER(ORDER BY A.INSERT_DT DESC) RNUM , A.* FROM ( SELECT A.COMPANY_CD , ISNULL(D.CNT, 0) AS CNT , B.COMPANY , B.ID , B.INDUSTRY_CD , ISNULL(C.NAME, ' ') INDUSTRY_NAME , B.CEO_NAME , B.TEL , A.INSERT_DT , C.DIVIDE_CD FROM HETB_CK_ANSWER A , HETB_CK_COMPANY B RIGHT OUTER JOIN HETB_CK_INDUSTRY C ON B.INDUSTRY_CD = C.INDUSTRY_CD RIGHT OUTER JOIN ( SELECT COMPANY_CD, COUNT(*) AS CNT FROM HETB_CK_HANDLEITEM GROUP BY COMPANY_CD ) D ON B.COMPANY_CD = D.COMPANY_CD WHERE A.MASTER_CD = $master_cd$ AND A.STATUS = 'N' AND A.COMPANY_CD = B.COMPANY_CD ) A
) A WHERE A.RNUM > #firstIndex# AND A.RNUM <= #firstIndex# + #recordCountPerPage#

sqlMap.update:
/* FreecheckAdminDAO.updateQuestion2 */
/* 질문 항목 수정 */ UPDATE HETB_CK_QUESTION2
SET QUESTION2 = '$question2$' , UPDATE_DT = GETDATE() , VALIDATION = #validation#
SET STATUS = 'Y' , UPDATE_DT = GETDATE() , VALIDATION = #validation#
WHERE QUESTION2_CD = $question2_cd$ AND QUESTION1_CD = $question1_cd$ AND MASTER_CD = $master_cd$

sqlMap.insert:
    /* FreecheckAdminDAO.addLinkedMaster */
    				
			  INSERT INTO HETB_CK_LINKED_MASTER ( MASTER_CD,INDUSTRY_CD,USE_YN,REG_DATE )
				VALUES ( #masterCd#, #industryCd#, #useYn#,  GETDATE() )

sqlMap.delete:
/* FreecheckAdminDAO.delLinkedMaster */
				
		DELETE FROM HETB_CK_LINKED_MASTER
		WHERE MASTER_CD = #masterCd# AND INDUSTRY_CD = #industryCd#

sqlMap.insert:
/* FreecheckAdminDAO.addItem */
SELECT CONVERT(INT, '0' + Max(ITEM_ID))+1 FROM HETB_CK_ITEM
INSERT INTO HETB_CK_ITEM ( ITEM_ID,ITEM_NAME,MASTER_CD,EXCEPT_NUM,REG_DATE,YEAR_CD ) VALUES ( #itemId#,#itemName#,#masterCd#,#exceptNum#,GETDATE(),#yearCd# )
