sqlMap.select:
/* FreecheckUserDAO.loginFreecheck */ SELECT A.* FROM ( SELECT ROW_NUMBER() OVER(ORDER BY COMPANY_CD DESC) RNUM , A.COMPANY_CD , A.ID , A.PASSWD , A.COMPANY , A.SA_NO , A.CEO_NAME , A.JUMIN , A.PHARMACIST_NO , A.TEL , A.FAX , A.HP , A.POST , A.ADDR1 , A.ADDR2 , A.EMAIL , A.INDUSTRY_CD , A.TYPE_CD_01_YN , A.TYPE_CD_02_YN , A.TYPE_CD_03_YN , A.TYPE_CD_04_YN , A.TYPE_CD_05_YN , A.TYPE_CD_06_YN , A.TYPE_CD_07_YN , A.TYPE_CD_08_YN , A.TYPE_CD_09_YN , A.TYPE_CD_10_YN , A.TYPE_CD_11_YN , A.INSERT_DT , A.INSERT_IP , A.INSERT_ID , A.UPDATE_DT , A.UPDATE_IP , A.UPDATE_ID , A.YEAR_CD , A.BLOG_URL , A.GRADE_CD , B.NAME INDUSTRY_NAME , B.DIVIDE_CD FROM HETB_CK_COMPANY A RIGHT OUTER JOIN HETB_CK_INDUSTRY B ON A.INDUSTRY_CD = B.INDUSTRY_CD WHERE A.STATUS = 'N' AND A.ID = '$id$'
AND PASSWD = #passwd#
) A WHERE A.RNUM = 1

/* FreecheckUserDAO.selectCheckStateList */
SELECT * FROM ( SELECT ROW_NUMBER() OVER(ORDER BY START_DT DESC) RNUM , A.* FROM ( SELECT A.MASTER_CD , A.TYPE_CD , A.TITLE , A.START_DT START_DT , A.END_DT END_DT , CASE WHEN B.COMPANY_CD IS NOT NULL THEN '점검완료' WHEN CONVERT(INT, CONVERT(CHAR(8), A.END_DT, 112)) < CONVERT(INT, CONVERT(CHAR(8), GETDATE(), 112)) THEN '기간초과' ELSE '미점검' END STATUS , A.INSERT_DT INSERT_DT FROM HETB_CK_MASTER A RIGHT OUTER JOIN HETB_CK_ANSWER B ON A.MASTER_CD = B.MASTER_CD LEFT OUTER JOIN HETB_CK_ANSWER C ON C.COMPANY_CD = $company_cd$ LEFT OUTER JOIN HETB_CK_ANSWER D ON D.STATUS = 'N' WHERE A.STATUS = 'N' AND A.YEAR_CD = '$year_cd$' AND CONVERT(CHAR(10), START_DT, 120) <= CONVERT(CHAR(10), GETDATE(), 120) AND A.TYPE_CD IN ($find_type_cd$)
) A ) WHERE RNUM > #firstIndex# AND RNUM <= #firstIndex# + #recordCountPerPage#

/* FreecheckUserDAO.selectCompanyInfoAnswer */
SELECT A.* FROM ( SELECT ROW_NUMBER() OVER(ORDER BY ANSWER_CD DESC) RNUM , ANSWER_CD , COMPANY_CD , COMPANY_ID , MASTER_CD , SANGHO_NAME , NUMBER1 , NUMBER2 , NUMBER3 , NAME1 , JUMIN1 , NAME2 , JUMIN2 , NAME3 , JUMIN3 , ADDR1 , ADDR2 , EMAIL , TEL , HP , FAX , HOMEPAGE , ROOM_CNT , WORK_TIME1 , WORK_TIME2 , WORK_TIME3 , CNT1 , CNT2 , CNT3 , CNT4 , CNT5 , CNT6 , CNT7 , CNT8 , CNT9 , ARTICLE1_YN , ARTICLE2_YN , ARTICLE3_YN , ARTICLE4_YN , ARTICLE5_YN , ARTICLE6_YN , ARTICLE7_YN , ARTICLE8_YN , ARTICLE9_YN , OTHER1 , OTHER2 , OTHER3 , OTHER4 , OTHER5 , OTHER6 , OTHER7 , OTHER8 , ATTACHFILE1 , ATTACHFILEORG1 , ATTACHTYPE1 , STATUS , INSERT_DT , INSERT_IP , INSERT_ID , UPDATE_DT , UPDATE_IP , UPDATE_ID FROM HETB_CK_ANSWER WHERE MASTER_CD = $master_cd$ AND STATUS = 'N'
AND COMPANY_CD = $company_cd$
) A WHERE A.RNUM = 1

/* FreecheckUserDAO.selectCompanyInfoAnswerList */ SELECT ROW_NUMBER() OVER(ORDER BY ANSWER_CD) RNUM , ANSWER_CD , COMPANY_CD , COMPANY_ID , MASTER_CD , SANGHO_NAME
, NAME1, NAME2, NAME3 , JUMIN1, JUMIN2, JUMIN3 , ADDR1, ADDR2 , EMAIL , TEL , HP , FAX , HOMEPAGE , ROOM_CNT , replace(WORK_TIME1, ',', '~') as WORK_TIME1 , replace(WORK_TIME2, ',', '~') as WORK_TIME2 , replace(WORK_TIME3, ',', '~') as WORK_TIME3 , CNT1 , CNT2 , CNT3 , CNT4 , CNT5 , CNT6 , CNT7 , CNT8 , CNT9 , ARTICLE1_YN , ARTICLE2_YN , ARTICLE3_YN , ARTICLE4_YN , ARTICLE5_YN , ARTICLE6_YN , ARTICLE7_YN , ARTICLE8_YN , ARTICLE9_YN , OTHER1 , OTHER2 , OTHER3 , OTHER4 , OTHER5 , OTHER6 , OTHER7 , OTHER8 , ATTACHFILE1 , ATTACHFILEORG1 , ATTACHTYPE1 , STATUS , INSERT_DT , INSERT_IP , INSERT_ID
, UPDATETIME_DT , UPDATETIME_IP , UPDATETIME_ID FROM HETB_CK_ANSWER WHERE MASTER_CD = $master_cd$ AND STATUS = 'N' /* ORDER BY SANGHO_NAME DESC */

sqlMap.update:
    /* FreecheckUserDAO.deleteHandleItem */
    	
    		DELETE 	FROM HETB_CK_HANDLEITEM
			WHERE 	HANDLE_CD = $handle_cd$
